import React, { useState, useEffect } from "react";

function App() {
  const [players, setPlayers] = useState(() => {
    const saved = localStorage.getItem("players");
    return saved ? JSON.parse(saved) : [];
  });

  const [newPlayer, setNewPlayer] = useState({
    name: "",
    phone: "",
    birthYear: "",
    position: "",
    imageUrl: "",
    joined: "",
    size: "",
    sessions: 0,
    totalSessions: 0,
    lastRenewal: ""
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setNewPlayer({ ...newPlayer, [name]: value });
  };

  const addPlayer = () => {
    if (!newPlayer.name || !newPlayer.birthYear || !newPlayer.joined) {
      alert("Ÿäÿ±ÿ¨Ÿâ ÿ™ÿπÿ®ÿ¶ÿ© ÿßŸÑÿßÿ≥ŸÖ Ÿàÿ≥ŸÜÿ© ÿßŸÑŸÖŸäŸÑÿßÿØ Ÿàÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿßŸÜÿ∂ŸÖÿßŸÖ ÿπŸÑŸâ ÿßŸÑÿ£ŸÇŸÑ.");
      return;
    }

    setPlayers((prev) => [...prev, { ...newPlayer }]);
    setNewPlayer({
      name: "",
      phone: "",
      birthYear: "",
      position: "",
      imageUrl: "",
      joined: "",
      size: "",
      sessions: 0,
      totalSessions: 0,
      lastRenewal: ""
    });
  };

  const markAttendance = (index) => {
    setPlayers((prev) => {
      const updated = [...prev];
      updated[index].sessions += 1;
      updated[index].totalSessions += 1;
      if (updated[index].sessions >= 12) {
        updated[index].sessions = 0;
        updated[index].lastRenewal = new Date().toISOString().split("T")[0];
      }
      return updated;
    });
  };

  const removeAttendance = (index) => {
    setPlayers((prev) => {
      const updated = [...prev];
      if (updated[index].sessions > 0) updated[index].sessions -= 1;
      if (updated[index].totalSessions > 0) updated[index].totalSessions -= 1;
      return updated;
    });
  };

  const deletePlayer = (index) => {
    const confirmDelete = window.confirm("ŸáŸÑ ÿ£ŸÜÿ™ ŸÖÿ™ÿ£ŸÉÿØ ŸÖŸÜ ÿ≠ÿ∞ŸÅ ÿßŸÑŸÑÿßÿπÿ®ÿü");
    if (confirmDelete) {
      setPlayers((prev) => prev.filter((_, i) => i !== index));
    }
  };

  useEffect(() => {
    localStorage.setItem("players", JSON.stringify(players));
  }, [players]);

  return (
    <div style={{ padding: 20, direction: "rtl" }}>
      <h2>üìã Point Academy - ÿ•ÿ∂ÿßŸÅÿ© ŸÑÿßÿπÿ® ÿ¨ÿØŸäÿØ</h2>
      <div style={{ display: "flex", flexDirection: "column", gap: 10, maxWidth: 400 }}>
        <input name="name" value={newPlayer.name} onChange={handleChange} placeholder="ÿßÿ≥ŸÖ ÿßŸÑŸÑÿßÿπÿ®" />
        <input name="phone" value={newPlayer.phone} onChange={handleChange} placeholder="ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ" />
        <input name="birthYear" value={newPlayer.birthYear} onChange={handleChange} placeholder="ÿ≥ŸÜÿ© ÿßŸÑŸÖŸäŸÑÿßÿØ" />
        <input name="position" value={newPlayer.position} onChange={handleChange} placeholder="ÿßŸÑŸÖÿ±ŸÉÿ≤ ŸÅŸä ÿßŸÑŸÖŸÑÿπÿ®" />
        <input name="size" value={newPlayer.size} onChange={handleChange} placeholder="ÿßŸÑŸÖŸÇÿßÿ≥" />
        <input name="joined" type="date" value={newPlayer.joined} onChange={handleChange} />
        <input name="imageUrl" value={newPlayer.imageUrl} onChange={handleChange} placeholder="ÿ±ÿßÿ®ÿ∑ ÿßŸÑÿµŸàÿ±ÿ© (ÿßÿÆÿ™Ÿäÿßÿ±Ÿä)" />
        <button onClick={addPlayer}>‚ûï ÿ•ÿ∂ÿßŸÅÿ©</button>
      </div>

      <hr style={{ margin: "20px 0" }} />

      <h3>üìë ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÑÿßÿπÿ®ŸäŸÜ</h3>
      {players.map((player, index) => (
        <div key={index} style={{ border: "1px solid #ccc", padding: 10, marginBottom: 10 }}>
          <strong>{player.name}</strong> ({player.birthYear}) - {player.position}
          <div>üì± {player.phone}</div>
          <div>üìè ÿßŸÑŸÖŸÇÿßÿ≥: {player.size}</div>
          <div>üóìÔ∏è ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿßŸÜÿ∂ŸÖÿßŸÖ: {player.joined}</div>
          <div>üèãÔ∏è‚Äç‚ôÇÔ∏è ÿßŸÑÿ≠ÿµÿµ ÿßŸÑÿ≠ÿßŸÑŸäÿ©: {player.sessions}</div>
          <div>üìä ŸÖÿ¨ŸÖŸàÿπ ÿßŸÑÿ≠ÿµÿµ: {player.totalSessions}</div>
          {player.lastRenewal && <div>‚úÖ ÿ¢ÿÆÿ± ÿ™ÿ¨ÿØŸäÿØ: {player.lastRenewal}</div>}
          <div style={{ marginTop: 10 }}>
            <button onClick={() => markAttendance(index)}>‚úÖ ÿ≠ÿ∂Ÿàÿ±</button>
            <button onClick={() => removeAttendance(index)} style={{ marginRight: 10 }}>‚Ü©Ô∏è ÿ™ÿ±ÿßÿ¨ÿπ</button>
            <button onClick={() => deletePlayer(index)} style={{ backgroundColor: "#fdd", color: "#900", marginRight: 10 }}>üóëÔ∏è ÿ≠ÿ∞ŸÅ</button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default App;
