import React, { useState, useEffect } from 'react';

const birthYears = [2010, 2011, 2012, 2013, 2014, 2015];
const sizeOptions = ["6-7Y", "8-9Y", "10-11Y", "12-13Y", "XS", "S", "M", "L", "XL", "XXL", "XXXL"];
const trainingDays = [6, 1, 3]; // Saturday, Monday, Wednesday

function calculateRenewalDate(startDate, attended) {
  if (!startDate) return "";
  const date = new Date(startDate);
  let count = attended;
  while (count < 12) {
    date.setDate(date.getDate() + 1);
    if (trainingDays.includes(date.getDay())) count++;
  }
  return date.toLocaleDateString();
}

function App() {
  const [players, setPlayers] = useState(() => {
    const saved = localStorage.getItem("players");
    return saved ? JSON.parse(saved) : [];
  });

  const [selectedYear, setSelectedYear] = useState(2013);
  const [newPlayer, setNewPlayer] = useState({
    name: "",
    phone: "",
    birthYear: 2013,
    position: "",
    imageUrl: "",
    joined: "",
    size: "6-7Y",
    lastRenewal: ""
  });

  useEffect(() => {
    localStorage.setItem("players", JSON.stringify(players));
  }, [players]);

  const markAttendance = (index) => {
    setPlayers((prev) => {
      const updated = [...prev];
      updated[index].sessions += 1;
      updated[index].totalSessions += 1;
      if (updated[index].sessions >= 12) {
        updated[index].sessions = 0;
        updated[index].lastRenewal = new Date().toISOString().split("T")[0];
      }
      return updated;
    });
  };

  const removeAttendance = (index) => {
    setPlayers((prev) => {
      const updated = [...prev];
      if (updated[index].sessions > 0) updated[index].sessions -= 1;
      if (updated[index].totalSessions > 0) updated[index].totalSessions -= 1;
      return updated;
    });
  };

  const addPlayer = () => {
    setPlayers((prev) => [
      ...prev,
      {
        ...newPlayer,
        sessions: 0,
        totalSessions: 0,
        lastRenewal: ""
      },
    ]);
    setNewPlayer({ name: "", phone: "", birthYear: 2013, position: "", imageUrl: "", joined: "", size: "6-7Y", lastRenewal: "" });
  };

  const deletePlayer = (index) => {
    const updated = [...players];
    updated.splice(index, 1);
    setPlayers(updated);
  };

  const filteredPlayers = players.filter((p) => p.birthYear === selectedYear);

  return (
    <div style={{ padding: '20px', fontFamily: 'sans-serif' }} dir="rtl">
      <h1>üìã Point Academy - ŸÉÿ¥ŸÅ ÿßŸÑÿ≠ÿ∂Ÿàÿ±</h1>

      <div style={{ marginBottom: '20px' }}>
        <label>ÿßÿÆÿ™ÿ± ÿ≥ŸÜÿ© ÿßŸÑŸÖŸäŸÑÿßÿØ: </label>
        <select value={selectedYear} onChange={(e) => setSelectedYear(parseInt(e.target.value))}>
          {birthYears.map((year) => (
            <option key={year} value={year}>{year}</option>
          ))}
        </select>
      </div>

      <div style={{ marginBottom: '20px', border: '1px solid #ccc', padding: '10px' }}>
        <h2>‚ûï ÿ•ÿ∂ÿßŸÅÿ© ŸÑÿßÿπÿ® ÿ¨ÿØŸäÿØ</h2>
        <input placeholder="ÿßÿ≥ŸÖ ÿßŸÑŸÑÿßÿπÿ®" value={newPlayer.name} onChange={(e) => setNewPlayer({ ...newPlayer, name: e.target.value })} />
        <input placeholder="ÿ±ŸÇŸÖ ÿßŸÑŸáÿßÿ™ŸÅ" value={newPlayer.phone} onChange={(e) => setNewPlayer({ ...newPlayer, phone: e.target.value })} />
        <input placeholder="ÿ≥ŸÜÿ© ÿßŸÑŸÖŸäŸÑÿßÿØ" value={newPlayer.birthYear} type="number" onChange={(e) => setNewPlayer({ ...newPlayer, birthYear: parseInt(e.target.value) })} />
        <input placeholder="ÿßŸÑŸÖÿ±ŸÉÿ≤" value={newPlayer.position} onChange={(e) => setNewPlayer({ ...newPlayer, position: e.target.value })} />
        <input placeholder="ÿ±ÿßÿ®ÿ∑ ÿµŸàÿ±ÿ© ÿßŸÑŸÑÿßÿπÿ®" value={newPlayer.imageUrl} onChange={(e) => setNewPlayer({ ...newPlayer, imageUrl: e.target.value })} />
        <input type="date" value={newPlayer.joined} onChange={(e) => setNewPlayer({ ...newPlayer, joined: e.target.value })} />
        <select value={newPlayer.size} onChange={(e) => setNewPlayer({ ...newPlayer, size: e.target.value })}>
          {sizeOptions.map((size) => (
            <option key={size} value={size}>{size}</option>
          ))}
        </select>
        <button onClick={addPlayer}>‚úÖ ÿ≠ŸÅÿ∏ ÿßŸÑŸÑÿßÿπÿ®</button>
      </div>

      {filteredPlayers.map((player, index) => (
        <div key={index} style={{ border: '1px solid #999', marginBottom: '15px', padding: '10px' }}>
          <h3>{player.name}</h3>
          <p>üì± {player.phone}</p>
          <p>‚öΩ ÿßŸÑŸÖÿ±ŸÉÿ≤: {player.position}</p>
          <p>üìè ÿßŸÑŸÖŸÇÿßÿ≥: {player.size}</p>
          <p>üèãÔ∏è‚Äç‚ôÇÔ∏è ÿßŸÑÿ≠ÿµÿµ ÿßŸÑÿ≠ÿßŸÑŸäÿ©: <strong>{player.sessions}</strong></p>
          <p>üìä ŸÖÿ¨ŸÖŸàÿπ ÿßŸÑÿ≠ÿµÿµ: {player.totalSessions}</p>
          <p>üóìÔ∏è ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿ™ÿ≥ÿ¨ŸäŸÑ: {player.joined}</p>
          <p>üóìÔ∏è ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿ™ÿ¨ÿØŸäÿØ ÿßŸÑŸÇÿßÿØŸÖ: {calculateRenewalDate(player.joined, player.sessions)}</p>
          {player.lastRenewal && <p>üí≥ ÿ¢ÿÆÿ± ÿ™ÿ¨ÿØŸäÿØ: {player.lastRenewal}</p>}
          {player.imageUrl && <img src={player.imageUrl} alt="Player" style={{ width: '100px', borderRadius: '8px' }} />}
          <div>
            <button onClick={() => markAttendance(index)}>‚úÖ ÿ≠ÿ∂Ÿàÿ±</button>
            <button onClick={() => removeAttendance(index)}>‚Ü©Ô∏è ÿ•ŸÑÿ∫ÿßÿ°</button>
            <button onClick={() => deletePlayer(index)}>üóëÔ∏è ÿ≠ÿ∞ŸÅ</button>
          </div>
        </div>
      ))}
    </div>
  );
}

export default App;
