import React, { useState, useEffect } from "react";
import { db } from "./firebase";
import {
  collection,
  addDoc,
  getDocs,
  updateDoc,
  deleteDoc,
  doc,
} from "firebase/firestore";

export default function PointAcademy() {
  const [players, setPlayers] = useState([]);
  const [newPlayer, setNewPlayer] = useState({
    name: "",
    phone: "",
    birthYear: "",
    position: "",
    joined: "",
    size: "",
    sessions: 0,
    totalSessions: 0,
    renewed: false,
  });

  const fetchPlayers = async () => {
    const querySnapshot = await getDocs(collection(db, "players"));
    const data = querySnapshot.docs.map((doc) => ({
      id: doc.id,
      ...doc.data(),
    }));
    setPlayers(data);
  };

  useEffect(() => {
    fetchPlayers();
  }, []);

  const handleAddPlayer = async () => {
    if (!newPlayer.name || !newPlayer.phone) return;
    try {
      await addDoc(collection(db, "players"), newPlayer);
      fetchPlayers();
      setNewPlayer({
        name: "",
        phone: "",
        birthYear: "",
        position: "",
        joined: "",
        size: "",
        sessions: 0,
        totalSessions: 0,
        renewed: false,
      });
    } catch (error) {
      console.error("Error adding player:", error);
    }
  };

  const handleDeletePlayer = async (id) => {
    await deleteDoc(doc(db, "players", id));
    fetchPlayers();
  };

  return (
    <div className="p-4 max-w-2xl mx-auto">
      <h1 className="text-2xl font-bold mb-4">🧒 تسجيل لاعب جديد</h1>
      <div className="grid grid-cols-2 gap-2 mb-4">
        <input
          placeholder="الاسم"
          value={newPlayer.name}
          onChange={(e) => setNewPlayer({ ...newPlayer, name: e.target.value })}
          className="border p-2"
        />
        <input
          placeholder="الهاتف"
          value={newPlayer.phone}
          onChange={(e) => setNewPlayer({ ...newPlayer, phone: e.target.value })}
          className="border p-2"
        />
        <input
          placeholder="سنة الميلاد"
          value={newPlayer.birthYear}
          onChange={(e) => setNewPlayer({ ...newPlayer, birthYear: e.target.value })}
          className="border p-2"
        />
        <input
          placeholder="المقاس"
          value={newPlayer.size}
          onChange={(e) => setNewPlayer({ ...newPlayer, size: e.target.value })}
          className="border p-2"
        />
        <input
          placeholder="المركز"
          value={newPlayer.position}
          onChange={(e) => setNewPlayer({ ...newPlayer, position: e.target.value })}
          className="border p-2"
        />
        <input
          placeholder="تاريخ الانضمام"
          value={newPlayer.joined}
          onChange={(e) => setNewPlayer({ ...newPlayer, joined: e.target.value })}
          className="border p-2"
        />
      </div>
      <button
        onClick={handleAddPlayer}
        className="bg-blue-600 text-white px-4 py-2 rounded"
      >
        إضافة لاعب
      </button>

      <hr className="my-6" />

      <h2 className="text-xl font-bold mb-2">📋 قائمة اللاعبين</h2>
      {players.map((player) => (
        <div key={player.id} className="border p-3 mb-2 rounded shadow">
          <div><strong>الاسم:</strong> {player.name}</div>
          <div><strong>الهاتف:</strong> {player.phone}</div>
          <div><strong>السنة:</strong> {player.birthYear}</div>
          <div><strong>المقاس:</strong> {player.size}</div>
          <div><strong>المركز:</strong> {player.position}</div>
          <div><strong>تاريخ الانضمام:</strong> {player.joined}</div>
          <div><strong>الحضور:</strong> {player.sessions} / 12</div>
          <button
            onClick={() => handleDeletePlayer(player.id)}
            className="mt-2 bg-red-500 text-white px-3 py-1 rounded"
          >
            حذف
          </button>
        </div>
      ))}
    </div>
  );
}
