import React, { useState, useEffect } from 'react';

function App() {
  const [players, setPlayers] = useState(() => {
    const savedPlayers = localStorage.getItem('players');
    return savedPlayers ? JSON.parse(savedPlayers) : [];
  });

  const [newPlayer, setNewPlayer] = useState({
    name: '',
    birthYear: '',
    phone: '',
    position: '',
    size: '',
    joinDate: '',
    image: ''
  });

  const [selectedPlayerId, setSelectedPlayerId] = useState(null);

  useEffect(() => {
    localStorage.setItem('players', JSON.stringify(players));
  }, [players]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewPlayer({ ...newPlayer, [name]: value });
  };

  const handleAddPlayer = (e) => {
    e.preventDefault();
    if (!newPlayer.name || !newPlayer.birthYear || !newPlayer.phone || 
        !newPlayer.position || !newPlayer.size || !newPlayer.joinDate) {
      alert('يرجى ملء جميع الحقول المطلوبة');
      return;
    }
    const player = {
      id: Date.now(),
      name: newPlayer.name,
      birthYear: newPlayer.birthYear,
      phone: newPlayer.phone,
      position: newPlayer.position,
      size: newPlayer.size,
      joinDate: newPlayer.joinDate,
      image: newPlayer.image,
      sessionsCurrent: 0,
      sessionsTotal: 0
    };
    setPlayers([...players, player]);
    setNewPlayer({
      name: '',
      birthYear: '',
      phone: '',
      position: '',
      size: '',
      joinDate: '',
      image: ''
    });
  };

  const markAttendance = (id) => {
    setPlayers(players.map(p => {
      if (p.id === id) {
        const newCurrent = (p.sessionsCurrent + 1) % 12;
        const newTotal = p.sessionsTotal + 1;
        return { ...p, sessionsCurrent: newCurrent, sessionsTotal: newTotal };
      }
      return p;
    }));
  };

  const undoAttendance = (id) => {
    setPlayers(players.map(p => {
      if (p.id === id) {
        const newCurrent = Math.max(p.sessionsCurrent - 1, 0);
        const newTotal = Math.max(p.sessionsTotal - 1, 0);
        return { ...p, sessionsCurrent: newCurrent, sessionsTotal: newTotal };
      }
      return p;
    }));
  };

  const deletePlayer = (id) => {
    const confirmDelete = window.confirm("هل أنت متأكد من حذف هذا اللاعب؟");
    if (confirmDelete) {
      setPlayers(players.filter(p => p.id !== id));
    }
  };

  const showDetails = (id) => {
    setSelectedPlayerId(id);
  };

  const closeDetails = () => {
    setSelectedPlayerId(null);
  };

  const groupedPlayers = players.reduce((groups, player) => {
    const year = player.birthYear;
    if (!groups[year]) {
      groups[year] = [];
    }
    groups[year].push(player);
    return groups;
  }, {});

  const sortedYears = Object.keys(groupedPlayers).sort((a, b) => parseInt(a) - parseInt(b));

  const selectedPlayer = selectedPlayerId 
    ? players.find(p => p.id === selectedPlayerId) 
    : null;

  return (
    <div className="App" dir="rtl" style={{ backgroundColor: '#e0f4ff', textAlign: 'right', minHeight: '100vh', padding: '20px' }}>
      <h2>إضافة لاعب جديد</h2>
      <form onSubmit={handleAddPlayer} style={{ marginBottom: '30px' }}>
        {[
          { label: 'الاسم', name: 'name' },
          { label: 'سنة الميلاد', name: 'birthYear', type: 'number' },
          { label: 'رقم الهاتف', name: 'phone', type: 'tel' },
          { label: 'المركز في الملعب', name: 'position' },
          { label: 'المقاس', name: 'size' },
          { label: 'تاريخ الانضمام', name: 'joinDate', type: 'date' },
          { label: 'رابط الصورة (اختياري)', name: 'image', type: 'url' },
        ].map(({ label, name, type = 'text' }) => (
          <div key={name} style={{ marginBottom: '10px' }}>
            <label>{label}:</label><br />
            <input 
              type={type}
              name={name}
              value={newPlayer[name]} 
              onChange={handleInputChange}
              required={name !== 'image'}
              style={{ padding: '5px', width: '100%' }} 
            />
          </div>
        ))}
        <button type="submit" style={{ padding: '10px 20px', fontWeight: 'bold' }}>إضافة اللاعب</button>
      </form>

      <h2>قائمة اللاعبين حسب المواليد</h2>
      {sortedYears.map(year => (
        <div key={year} style={{ marginBottom: '20px' }}>
          <h3>{year}</h3>
          <ul style={{ listStyle: 'none', paddingLeft: 0 }}>
            {groupedPlayers[year].map(player => (
              <li key={player.id} style={{ marginBottom: '10px' }}>
                <strong 
                  onClick={() => showDetails(player.id)} 
                  style={{ cursor: 'pointer', marginLeft: 10 }}
                >
                  {player.name}
                </strong>
                - الحصص الحالية: {player.sessionsCurrent} 
                - المجموع: {player.sessionsTotal}
                <button onClick={() => markAttendance(player.id)} style={{ marginRight: 10 }}>تسجيل حضور</button>
                <button onClick={() => undoAttendance(player.id)} style={{ marginRight: 10 }}>تراجع</button>
                <button onClick={() => showDetails(player.id)} style={{ marginRight: 10 }}>تفاصيل</button>
                <button onClick={() => deletePlayer(player.id)} style={{ backgroundColor: '#ffcccc', color: '#900' }}>حذف</button>
              </li>
            ))}
          </ul>
        </div>
      ))}

      {selectedPlayer && (
        <div 
          onClick={closeDetails} 
          style={{ 
            position: 'fixed', top: 0, left: 0, right: 0, bottom: 0, 
            backgroundColor: 'rgba(0, 0, 0, 0.5)', 
            display: 'flex', alignItems: 'center', justifyContent: 'center' 
          }}
        >
          <div 
            onClick={e => e.stopPropagation()} 
            style={{ 
              backgroundColor: '#fff', padding: '20px', borderRadius: '8px', 
              width: '300px', maxWidth: '90%' 
            }}
          >
            <h3>{selectedPlayer.name}</h3>
            {selectedPlayer.image && (
              <img 
                src={selectedPlayer.image} 
                alt={selectedPlayer.name} 
                style={{ maxWidth: '100%', marginBottom: '10px' }} 
              />
            )}
            <p>سنة الميلاد: {selectedPlayer.birthYear}</p>
            <p>رقم الهاتف: {selectedPlayer.phone}</p>
            <p>المركز: {selectedPlayer.position}</p>
            <p>المقاس: {selectedPlayer.size}</p>
            <p>تاريخ الانضمام: {selectedPlayer.joinDate}</p>
            <p>الحصص الحالية: {selectedPlayer.sessionsCurrent}</p>
            <p>المجموع الكلي: {selectedPlayer.sessionsTotal}</p>
            <button onClick={closeDetails} style={{ marginTop: 10 }}>إغلاق</button>
          </div>
        </div>
      )}
    </div>
  );
}

export default App;
