import React, { useState, useEffect } from "react";

export default function PointAcademy() {
  const [players, setPlayers] = useState(() => {
    const saved = localStorage.getItem("players");
    return saved ? JSON.parse(saved) : [];
  });

  const [newPlayer, setNewPlayer] = useState({
    name: "",
    phone: "",
    birthYear: "",
    position: "",
    joined: "",
    size: "",
    sessions: 0,
    renewed: false
  });

  useEffect(() => {
    localStorage.setItem("players", JSON.stringify(players));
  }, [players]);

  const handleAdd = () => {
    setPlayers(prev => [
      ...prev,
      {
        ...newPlayer,
        id: Date.now(),
        sessions: 0,
        renewed: false
      }
    ]);
    setNewPlayer({
      name: "",
      phone: "",
      birthYear: "",
      position: "",
      joined: "",
      size: "",
      sessions: 0,
      renewed: false
    });
  };

  const handleAttendance = (id, type) => {
    setPlayers(prev =>
      prev.map(p =>
        p.id === id
          ? {
              ...p,
              sessions: type === "add" ? p.sessions + 1 : Math.max(0, p.sessions - 1)
            }
          : p
      )
    );
  };

  const toggleRenewed = (id) => {
    setPlayers(prev =>
      prev.map(p =>
        p.id === id ? { ...p, renewed: !p.renewed } : p
      )
    );
  };

  const grouped = players.reduce((acc, p) => {
    acc[p.birthYear] = acc[p.birthYear] || [];
    acc[p.birthYear].push(p);
    return acc;
  }, {});

  const sortedYears = Object.keys(grouped).sort();

  return (
    <div style={{ padding: "20px", fontFamily: "Arial", direction: "rtl" }}>
      <h2>üìã ÿ•ÿ∂ÿßŸÅÿ© ŸÑÿßÿπÿ® ÿ¨ÿØŸäÿØ</h2>
      <input placeholder="ÿßŸÑÿßÿ≥ŸÖ" value={newPlayer.name} onChange={e => setNewPlayer({ ...newPlayer, name: e.target.value })} />
      <input placeholder="ÿßŸÑŸáÿßÿ™ŸÅ" value={newPlayer.phone} onChange={e => setNewPlayer({ ...newPlayer, phone: e.target.value })} />
      <input placeholder="ÿ≥ŸÜÿ© ÿßŸÑŸÖŸäŸÑÿßÿØ" value={newPlayer.birthYear} onChange={e => setNewPlayer({ ...newPlayer, birthYear: e.target.value })} />
      <input placeholder="ÿßŸÑŸÖÿ±ŸÉÿ≤" value={newPlayer.position} onChange={e => setNewPlayer({ ...newPlayer, position: e.target.value })} />
      <input type="date" value={newPlayer.joined} onChange={e => setNewPlayer({ ...newPlayer, joined: e.target.value })} />
      <input placeholder="ÿßŸÑŸÖŸÇÿßÿ≥" value={newPlayer.size} onChange={e => setNewPlayer({ ...newPlayer, size: e.target.value })} />
      <button onClick={handleAdd}>‚ûï ÿ•ÿ∂ÿßŸÅÿ©</button>

      <h2 style={{ marginTop: "30px" }}>üìå ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÑÿßÿπÿ®ŸäŸÜ</h2>
      {sortedYears.map(year => (
        <div key={year}>
          <h3>üìÖ ŸÖŸàÿßŸÑŸäÿØ {year}</h3>
          {grouped[year].map(player => (
            <div key={player.id} style={{
              border: "1px solid #ccc",
              padding: "15px",
              margin: "10px 0",
              borderRadius: "10px",
              background: "#f9f9f9"
            }}>
              <strong>{player.name}</strong> - {player.phone}<br />
              ÿßŸÑŸÖÿ±ŸÉÿ≤: {player.position} | ÿßŸÑŸÖŸÇÿßÿ≥: {player.size} | ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿßŸÜÿ∂ŸÖÿßŸÖ: {player.joined}<br />
              ‚úÖ ÿßŸÑÿ≠ÿµÿµ: {player.sessions}
              <div style={{ marginTop: "10px" }}>
                <button onClick={() => handleAttendance(player.id, "add")} style={{ marginRight: "5px" }}>‚ûï ÿ≠ÿ∂Ÿàÿ±</button>
                <button onClick={() => handleAttendance(player.id, "remove")} style={{ marginRight: "5px" }}>‚Ü©Ô∏è ÿ™ÿ±ÿßÿ¨ÿπ</button>
                <button
                  onClick={() => toggleRenewed(player.id)}
                  style={{
                    backgroundColor: player.renewed ? "green" : "gray",
                    color: "white",
                    padding: "5px 10px"
                  }}
                >
                  {player.renewed ? "‚úÖ ŸÖÿ¨ÿØÿØ" : "üîÅ ÿ™ÿ¨ÿØŸäÿØ"}
                </button>
              </div>
            </div>
          ))}
        </div>
      ))}
    </div>
  );
}
