import React, { useState, useEffect } from "react";
import { initializeApp } from "firebase/app";
import { getStorage, ref, uploadBytes, getDownloadURL } from "firebase/storage";

const firebaseConfig = {
  apiKey: "AIzaSyDNTwldxdyE7JOv8KwLcaWblo1AKvKEnQA",
  authDomain: "pointacademykwt.firebaseapp.com",
  projectId: "pointacademykwt",
  storageBucket: "pointacademykwt.appspot.com",
  messagingSenderId: "645987319659",
  appId: "1:645987319659:web:935e281db66c628bc4d62e"
};

const app = initializeApp(firebaseConfig);
const storage = getStorage(app);

export default function App() {
  const [players, setPlayers] = useState(() => {
    const saved = localStorage.getItem("players");
    return saved ? JSON.parse(saved) : [];
  });

  const [newPlayer, setNewPlayer] = useState({
    name: "",
    phone: "",
    birthYear: "",
    position: "",
    joined: "",
    size: "",
    image: ""
  });

  const [image, setImage] = useState(null);
  const [uploading, setUploading] = useState(false);

  useEffect(() => {
    localStorage.setItem("players", JSON.stringify(players));
  }, [players]);

  const handleUpload = async () => {
    if (!image) return alert("ÿßÿÆÿ™ÿ± ÿµŸàÿ±ÿ© ÿ£ŸàŸÑÿßŸã");
    setUploading(true);
    const imageRef = ref(storage, `player-images/${Date.now()}_${image.name}`);
    await uploadBytes(imageRef, image);
    const downloadURL = await getDownloadURL(imageRef);
    setNewPlayer(prev => ({ ...prev, image: downloadURL }));
    setUploading(false);
  };

  const handleAddPlayer = () => {
    setPlayers(prev => [...prev, { ...newPlayer, id: Date.now() }]);
    setNewPlayer({
      name: "",
      phone: "",
      birthYear: "",
      position: "",
      joined: "",
      size: "",
      image: ""
    });
    setImage(null);
  };

  return (
    <div style={{ padding: 40, fontFamily: "Arial", direction: "rtl" }}>
      <h2>üßí ÿ•ÿ∂ÿßŸÅÿ© ŸÑÿßÿπÿ® ÿ¨ÿØŸäÿØ</h2>
      <input placeholder="ÿßŸÑÿßÿ≥ŸÖ" value={newPlayer.name} onChange={e => setNewPlayer({ ...newPlayer, name: e.target.value })} />
      <input placeholder="ÿßŸÑŸáÿßÿ™ŸÅ" value={newPlayer.phone} onChange={e => setNewPlayer({ ...newPlayer, phone: e.target.value })} />
      <input placeholder="ÿ≥ŸÜÿ© ÿßŸÑŸÖŸäŸÑÿßÿØ" value={newPlayer.birthYear} onChange={e => setNewPlayer({ ...newPlayer, birthYear: e.target.value })} />
      <input placeholder="ÿßŸÑŸÖÿ±ŸÉÿ≤" value={newPlayer.position} onChange={e => setNewPlayer({ ...newPlayer, position: e.target.value })} />
      <input placeholder="ÿ™ÿßÿ±ŸäÿÆ ÿßŸÑÿßŸÜÿ∂ŸÖÿßŸÖ" type="date" value={newPlayer.joined} onChange={e => setNewPlayer({ ...newPlayer, joined: e.target.value })} />
      <input placeholder="ÿßŸÑŸÖŸÇÿßÿ≥" value={newPlayer.size} onChange={e => setNewPlayer({ ...newPlayer, size: e.target.value })} />
      <input type="file" onChange={(e) => setImage(e.target.files[0])} />
      <button onClick={handleUpload} disabled={uploading}>
        {uploading ? "ÿ¨ÿßÿ±Ÿä ÿ±ŸÅÿπ ÿßŸÑÿµŸàÿ±ÿ©..." : "üì§ ÿ±ŸÅÿπ ÿßŸÑÿµŸàÿ±ÿ©"}
      </button>
      <br /><br />
      <button onClick={handleAddPlayer}>‚ûï ÿ≠ŸÅÿ∏ ÿßŸÑŸÑÿßÿπÿ®</button>

      <h3 style={{ marginTop: 30 }}>üìã ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÑÿßÿπÿ®ŸäŸÜ</h3>
      {players.map(player => (
        <div key={player.id} style={{ border: "1px solid #ccc", borderRadius: "8px", padding: 10, marginTop: 10 }}>
          <strong>{player.name}</strong> | üéÇ {player.birthYear} | üì± {player.phone}<br />
          üèüÔ∏è {player.position} | üìè {player.size} | üóìÔ∏è {player.joined}<br />
          {player.image && <img src={player.image} alt="player" style={{ width: 100, marginTop: 10 }} />}
        </div>
      ))}
    </div>
  );
}
