import React, { useState, useEffect } from "react";

export default function PointAcademy() {
  const [players, setPlayers] = useState(() => {
    const saved = localStorage.getItem("players");
    return saved ? JSON.parse(saved) : [];
  });

  const [newPlayer, setNewPlayer] = useState({
    name: "",
    phone: "",
    birthYear: "",
    position: "",
    joined: "",
    size: ""
  });

  const [expandedYears, setExpandedYears] = useState([]);
  const [showYearInput, setShowYearInput] = useState(false);

  useEffect(() => {
    localStorage.setItem("players", JSON.stringify(players));
  }, [players]);

  const handleAdd = () => {
    setPlayers(prev => [...prev, {
      ...newPlayer,
      id: Date.now(),
      renewed: false,
      sessions: 0,
      attendanceCycle: 0
    }]);
    setNewPlayer({ name: "", phone: "", birthYear: "", position: "", joined: "", size: "" });
  };

  const toggleYear = (year) => {
    setExpandedYears(prev =>
      prev.includes(year) ? prev.filter(y => y !== year) : [...prev, year]
    );
  };

  const markRenewed = (id, value) => {
    setPlayers(prev =>
      prev.map(player =>
        player.id === id ? { ...player, renewed: value } : player
      )
    );
  };

  const markAttendance = (id) => {
    setPlayers(prev =>
      prev.map(player => {
        if (player.id === id) {
          const newSessions = player.sessions + 1;
          if (newSessions >= 12) {
            return {
              ...player,
              sessions: 0,
              attendanceCycle: (player.attendanceCycle || 0) + 1
            };
          }
          return { ...player, sessions: newSessions };
        }
        return player;
      })
    );
  };

  const undoAttendance = (id) => {
    setPlayers(prev =>
      prev.map(player =>
        player.id === id && player.sessions > 0
          ? { ...player, sessions: player.sessions - 1 }
          : player
      )
    );
  };

  const deletePlayer = (id) => {
    setPlayers(prev => prev.filter(p => p.id !== id));
  };

  const grouped = players.reduce((acc, p) => {
    acc[p.birthYear] = acc[p.birthYear] || [];
    acc[p.birthYear].push(p);
    return acc;
  }, {});

  const sortedYears = Object.keys(grouped).sort();

  return (
    <div style={{ padding: "20px", fontFamily: "Arial", direction: "rtl" }}>
      <h2>üìã ÿ•ÿ∂ÿßŸÅÿ© ŸÑÿßÿπÿ® ÿ¨ÿØŸäÿØ</h2>
      <input placeholder="ÿßŸÑÿßÿ≥ŸÖ" value={newPlayer.name} onChange={e => setNewPlayer({ ...newPlayer, name: e.target.value })} />
      <input placeholder="ÿßŸÑŸáÿßÿ™ŸÅ" value={newPlayer.phone} onChange={e => setNewPlayer({ ...newPlayer, phone: e.target.value })} />
      <input placeholder="ÿßŸÑŸÖÿ±ŸÉÿ≤" value={newPlayer.position} onChange={e => setNewPlayer({ ...newPlayer, position: e.target.value })} />
      <input type="date" value={newPlayer.joined} onChange={e => setNewPlayer({ ...newPlayer, joined: e.target.value })} />
      <input placeholder="ÿßŸÑŸÖŸÇÿßÿ≥" value={newPlayer.size} onChange={e => setNewPlayer({ ...newPlayer, size: e.target.value })} />
      {!showYearInput && <button onClick={() => setShowYearInput(true)}>ÿßÿÆÿ™ÿ± ÿ≥ŸÜÿ© ÿßŸÑŸÖŸäŸÑÿßÿØ</button>}
      {showYearInput && (
        <input placeholder="ÿ≥ŸÜÿ© ÿßŸÑŸÖŸäŸÑÿßÿØ" value={newPlayer.birthYear} onChange={e => setNewPlayer({ ...newPlayer, birthYear: e.target.value })} />
      )}
      <button onClick={handleAdd}>‚ûï ÿ•ÿ∂ÿßŸÅÿ©</button>

      <h2 style={{ marginTop: "30px" }}>üìå ŸÇÿßÿ¶ŸÖÿ© ÿßŸÑŸÑÿßÿπÿ®ŸäŸÜ ÿ≠ÿ≥ÿ® ÿßŸÑŸÖŸàÿßŸÑŸäÿØ</h2>
      {sortedYears.map(year => (
        <div key={year}>
          <h3 style={{ cursor: "pointer", color: "blue" }} onClick={() => toggleYear(year)}>
            ‚ñ∂Ô∏è {year}
          </h3>
          {expandedYears.includes(year) && (
            <ul>
              {grouped[year].map(player => (
                <li key={player.id} style={{ margin: "10px 0", border: "1px solid #ccc", padding: "10px", borderRadius: "8px" }}>
                  <div><strong>üë§ {player.name}</strong></div>
                  <div>üìû {player.phone}</div>
                  <div>üéØ ÿßŸÑŸÖÿ±ŸÉÿ≤: {player.position}</div>
                  <div>üìè ÿßŸÑŸÖŸÇÿßÿ≥: {player.size}</div>
                  <div>üìÖ ÿßŸÜÿ∂ŸÖ ŸÅŸä: {player.joined}</div>
                  <div>üìà ÿπÿØÿØ ÿßŸÑÿ≠ÿµÿµ: {player.sessions} (ÿßŸÑÿØŸàÿ±ÿ© #{player.attendanceCycle || 0})</div>
                  <div style={{ display: "flex", gap: "10px", marginTop: "8px" }}>
                    <button onClick={() => markAttendance(player.id)}>‚úÖ ÿ≠ÿ∂Ÿàÿ±</button>
                    <button onClick={() => undoAttendance(player.id)}>‚Ü©Ô∏è ÿ™ÿ±ÿßÿ¨ÿπ</button>
                    <button onClick={() => markRenewed(player.id, !player.renewed)} style={{ backgroundColor: player.renewed ? "green" : "gray", color: "#fff" }}>
                      {player.renewed ? "‚úÖ ŸÖÿ¨ÿØÿØ" : "‚ùå ŸÑŸÖ Ÿäÿ¨ÿØÿØ"}
                    </button>
                    <button onClick={() => deletePlayer(player.id)} style={{ backgroundColor: "#900", color: "#fff" }}>
                      üóëÔ∏è ÿ≠ÿ∞ŸÅ
                    </button>
                  </div>
                </li>
              ))}
            </ul>
          )}
        </div>
      ))}
    </div>
  );
}
